import React from 'react';
import './card.css';
import Img from '../img/Img';

class Card extends React.Component {
    render() {
        const name = this.props.name;
        const description = this.props.description;
        const imgSrc = this.props.src;
        const projectLink = this.props.projectLink;
        const githubLink = this.props.githubLink;
        // const target = this.props.target;
        return(
            <React.Fragment>
                <div className='column project-column'>
                    <div className='project-card is-12-mobile is-half-tablet is-one-third-desktop'>
                        <div className='img-div'>
                            <Img src={imgSrc}/>
                            <div className='overlay'> 
                                <div className='card-info'>
                                    <p>{description}</p>
                                    <button href={githubLink} className='github-link'>Github <i className="fab fa-github"></i></button>
                                    <button href={projectLink} className='demo-link'>Demo <i className="fas fa-chevron-right"></i></button>
                                </div>
                            </div>   
                        </div>
                    </div>

                    <p className='name'>{name}</p>
                </div>   
            </React.Fragment>
        ) 
    }
}

export default Card;
